public void save(){
    String data = "data";
    FileOutputStream out = null;
    BufferWriter writer= null;
    try{
        out = openFileOutput("data", Context.MODE_PRIVATE);
        writer = new BufferWriter(new OutputStreamWriter(out));
        writer.write(data);
    }catch(IOException e){
        e.printStacktrace();
    }finally{
        try{
            if(writer != null){
                writer.close();
            }catch(IOException e){
                e.printStacktrace();
            }
        }
    }
}

