ViewHolder可以用于缓存子项内部的实例，免得每次加载子项都要去findViewById()
class FruitAdapter extends ArrayAdapter{
    // 子项布局文件的id
    private int resId;

    public FruitAdapter(Context context, int textViewResourseId, List objects){
        super(context, textViewResourseId, objects);
        this.resid = textViewResourseId;
    }

    @override
    public View getView(int position, View convertView, ViewGroup, viewgroup){
        Fruit fruit = getItem(position);
        View view ;

         ViewHolder viewholder;
        // 如果之前没有缓存过布局
        if(convertView == null){
            view = LayoutInflater.from(getContext()).inflate(resId,parent,false);
            viewHolder = new ViewHolder();
            viewholder.textview = view.getViewById(R.id.text);
            viewholder.imageview = view.getViewById(R.id.image);
            // 将ViewHolder保存在view中
            view.saveTag(viewholder);
        }
        // 如果不为空，直接取来用
        else{
            view = convertView；
            // 重新获取VIewholder
            viewholder = view.getTag();
        }
        viewholder.imageview.setImageResource(fruit.getRPicId());
        viewholder.textview.setText(fruit.getName());
        return view;
    }

    class ViewHolder{
        // 缓存实例
        TextView textview;
        ImageView imageview;
    }
}

