        // 联系人号码
        String displayNum  = cursor.getString(cursor.getColumnIndex(ContactsContract.CommonDatakinds.Phone.NUMBER));
      }
    }
  }
  catch(Exception e){
    e.printStackTrace();
  }
  finally{
     cursor.close();
  }
}
