定义回调接口
DownloadListener.java

package com.example.sqlite_demo.Interface;

/**
 * 下载回调接口
 * 用于监听各种状态的监听和回调
 */
public interface DownloadListener {

    /**
     * 进度百分比回调方法
     * @param progress 进度(0-100)
     */
    void onProgress(int progress);

    /**
     * 成功回调方法
     */
    void onSuccess();

    /**
     * 失败回调接口
     */
    void onFailed();

    /**
     * 暂停回调方法
     */
    void onPause();

    /**
     * 取消回调方法
     */
    void onCanceled();
}

下载功能 线程 AsyncTask
DownTask.java

public class DownloadTask extends AsyncTask     {
    private static final int TYPE_SUCCESS = 0;
    private static final int TYPE_FAILED = 1;
    private static final int TYPE_PAUSED = 2;
    private static final int TYPE_CANCELED = 3;

    private DownloadListener downloadListener;
    private boolean isCancel;
    private boolean isPause;
    private int lastProgress;

    public DownloadTask(DownloadListener downloadListener) {
        this.downloadListener = downloadListener;
    }

    public void setPause(boolean pause) {
        isPause = pause;
    }

    public void setCancel(boolean cancel) {
        isCancel = cancel;
    }

    @Override
    protected Integer doInBackground(String... strings) {
        InputStream inputStream = null;
        RandomAccessFile savedFile = null;
        File file = null;
        try{
            // 已下载的文件长度
            long downloadedLength = 0;
            String downloadURL = strings[0];
            String[] subStrings = downloadURL.split("/");
            String fileName =subStrings[subStrings.length-1];
            String directory = MyApp.getContext().getApplicationContext().getFilesDir().getAbsolutePath();
            file = new File(directory+ fileName);
            Log.d("DS",directory);
            Log.d("DS",fileName);
            if (file.exists()){
                boolean isDeleted = file.delete();
                downloadedLength = file.length();
            }
            long contentLength = getContentLength(downloadURL);
            if (contentLength == 0){
                // 下载失败
                return TYPE_FAILED;
            }else if(contentLength == downloadedLength){
                // 下载成功
                return TYPE_SUCCESS;
            }
            // 网络操作
            OkHttpClient client = new OkHttpClient();
            Request request = new Request.Builder()
                    // 支持断点下载
                    .addHeader("RANGE", "bytes="+ downloadedLength +"-")
                    .url(downloadURL)
                    .build();
            Response response = client.newCall(request).execute();
            if (response.body() != null) {
                inputStream = response.body().byteStream();
                savedFile = new RandomAccessFile(file, "rw");
                // 跳过已经下载的字节
                savedFile.seek(downloadedLength);
                byte[] buf = new byte[1024];
                int total = 0;
                int len;
                // 正在下载
                while ((len = inputStream.read(buf)) != -1){
                    if(isCancel)    return TYPE_CANCELED;
                    else if(isPause)    return TYPE_PAUSED;
                    else{
                        total += len;
                        savedFile.write(buf, 0, len);
                        // 计算已经下载的百分比
                        int progress = (int)((total+downloadedLength) * 100 / contentLength);
                        publishProgress(progress);
                    }
                }
                response.body().close();
                return TYPE_SUCCESS;
            }
        }catch (Exception e){
            e.printStackTrace();
        }finally {
            if (inputStream != null) {
                try {
                    inputStream.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            if (savedFile != null){
                try {
                    savedFile.close();
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
            if (isCancel && file != null){
                boolean delete = file.delete();
            }
        }
        return TYPE_FAILED;
    }

    private long getContentLength(String downloadURL) throws IOException {
        OkHttpClient client = new OkHttpClient();
        Request request = new Request.Builder()
                .url(downloadURL)
                .build();
        Response response = client.newCall(request).execute();
        if (response.body() != null && response.isSuccessful()){
            long contentLength = response.body().contentLength();
            response.body().close();
            return contentLength;
        }
        return 0;
    }
    @Override
    protected void onPostExecute(Integer integer) {
        switch (integer){
            case TYPE_SUCCESS:
                downloadListener.onSuccess();
                break;
            case TYPE_FAILED:
                downloadListener.onFailed();
                break;
            case TYPE_PAUSED:
                downloadListener.onPause();
                break;
            case TYPE_CANCELED:
                downloadListener.onCanceled();
                break;
            default:
                break;
        }
    }

    @Override
    protected void onProgressUpdate(Integer... values) {
        int progress = values[0];
        if (progress > lastProgress){
            downloadListener.onProgress(progress);
            lastProgress = progress;
        }
    }

    @Override
    protected void onCancelled(Integer integer) {
        super.onCancelled(integer);
    }
}

